import React, { Component } from "react";
import { render } from "react-dom";
import "./style.css";
import { TextBox } from "./TextBox";
import { Label } from "./Label";
import { Button } from "./Button";
const axios =require('axios');

class App extends Component {

  render() {
    return (
      <div className="container">
        <h1> My Atos </h1>
        <form >
          <Label forname="empname" name="Employee name" />
          <TextBox type="text" name="empname" id="emp_name" />
          <Label forname="email" name="Email" />
          <TextBox type="email" name="email" id="email_" />
          <Label forname="empid" name="Employee ID" />
          <TextBox type="text" pattern="[5]{1}[0-9]{6}" name="number" id="empid_" />
          <Label forname="number1" name="Phone number" />
          <TextBox type="tel" pattern="[7-9]{1}[0-9]{4}[0-9]{5}" name="tel" id="number_1" />
          <Button onSubmit={(e) => register(e)}/>
        </form>
      </div>
    );
  }
}
function register(e) {
  e.preventDefault();
  let request = {
    empname1: document.getElementById("emp_name").value,
    email1: document.getElementById("email_").value,
    empid1: document.getElementById("empid_").value,
    phonenumber1: document.getElementById("number_1").value,
  }
  axios.post('http://localhost:3000/register', request)
    .then(resp => {
      console.log("Employee name:")
      alert(resp.data.empname);
      console.log("Email id:")
      alert(resp.data.email);
      console.log("Employee ID:")
      alert(resp.data.empid);
      console.log("Phone number:")
      alert(resp.data.phonenumber);
    })
}
render(<App />, document.getElementById("root"));
